mapper.update:
UPDATE base_admin_user


    sys_user_name = #{sysUserName},


    sys_user_pwd = #{sysUserPwd},


    role_id = #{roleId},


    user_phone = #{userPhone}


WHERE id = #{id}

UPDATE base_admin_user
set sys_user_pwd = #{password}
where sys_user_name = #{userName}

UPDATE base_admin_user
SET user_status = #{status}
WHERE id = #{id}

mapper.select:
SELECT 
FROM base_admin_user
WHERE sys_user_name = #{sysUserName}
and user_status = 1

  and id != #{id}

SELECT 
FROM base_admin_user
WHERE user_phone = #{userPhone}

  and id != #{id}

SELECT count(id)
FROM base_student
WHERE school_student_email = #{Email}

SELECT count(id)
FROM base_school
WHERE school_manager_email = #{Email}

SELECT count(id)
FROM base_company
WHERE company_contacts_email = #{Email}

SELECT count(keyid)
FROM base_school
WHERE school_manager_phone = #{phone}

SELECT count(id)
FROM base_company
WHERE company_contacts_phone = #{phone}

SELECT id, sys_user_name AS sysUserName ,role_id AS roleId,
(SELECT role_name FROM base_admin_role as r WHERE r.id = u.role_id) as roleName,
user_phone AS userPhone,reg_time AS regTime,user_status AS userStatus
FROM base_admin_user as u


    and sys_user_name LIKE '%' #{userSearchDTO.sysUserName} '%'


    and user_phone  LIKE '%' #{userSearchDTO.userPhone} '%'


    and reg_time >  #{userSearchDTO.startTime}


    and reg_time <  #{userSearchDTO.endTime}


limit #{pageNum,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
